import React, {Component, useState} from 'react';
import './App.css';

import { Icon, Menu, Segment, Button, Label, Grid, Image, Modal } from 'semantic-ui-react'

import tsne from './tsne.png'
import logo from './logo.svg'

const VisualizationComponent = ({

}) => {

  return (
    <Segment attached='top' style={{height: '90%'}}>
      <Grid style={{height: '100%'}}>
        <Grid.Column width={12}>
          <Image size='small' src={tsne} />
        </Grid.Column>
        <Grid.Column width={4} style={{height: '100%'}}>
          <Segment style={{height: '50%'}} content='legend' placeholder/>
          <Segment style={{height: '50%'}} content='logs' placeholder/>
        </Grid.Column>
      </Grid>
    </Segment>
  )
}

const App = ({

}) => {
    return (
      <Segment style={{height: '100%'}}>


        <Segment attached='bottom' style={{height: '10%'}} as={Menu} size='big'>
          <Menu.Item
            header
            icon='database'
            iconPosition='left'
            content='CReSCENT'
          />
          <Modal
            trigger={
              <Menu.Item content={
                <Button color='red' content='Upload' icon='upload'/>
              } />
            }
            header='Upload Workflow Inputs Here'
            content='Upload API stuff here'
            actions={
              [
                <Button key='submit' content='Submit uploads' />
              ]
            }
          />
          <Menu.Item />
          <Menu.Menu>
          {
            ['Alignment', 'Normalization', 'Clustering'].map(
              step => (


                    <Modal
                      trigger={
                        <Menu.Item key={step} content={
                          <Button content={step} fluid />
                        } />
                      }
                      header={step}
                      content={`${step} parameters`}
                      actions={[
                        <Button key='close' content='close' />
                      ]}
                    />

              )
            )
          }
          </Menu.Menu>
        </Segment>
      </Segment>
    )
  }

  export default App
