import React, {Component, useState} from 'react';
import './App.css';

import { Icon, Menu, Dropdown, Segment, Button, Label, Grid, Image, Modal, Divider } from 'semantic-ui-react'

import tsne from './tsne.png'
import logo from './logo.svg'

const VisualizationComponent = ({
  visContent,
  setVisContent
}) => {

  return (
    <Segment attached='top' style={{height: '90%'}}>
      <Grid style={{height: '100%'}}>
        <Grid.Column width={12}>
          <Image size='small' src={tsne} />
          {
            visContent=='Home' ?
            'Home plot here'
            : visContent=='Alignment' ?
            'Alignment plot here'
            : visContent=='Normalization' ?
            'Normalization plot here'
            : visContent=='Clustering' ?
            'Clustering plot here'
            : 'No plots'
          }
          <Divider />
        </Grid.Column>
        <Grid.Column width={4} style={{height: '100%'}}>
          <Segment style={{height: '50%'}}
            content={`${visContent} legend`} placeholder
          />
          <Segment style={{height: '50%'}} 
            content={`${visContent} logs`} placeholder
          />
        </Grid.Column>
      </Grid>
    </Segment>
  )
}

const MenuComponent = ({
  visContent,
  setVisContent
}) => {

  return (
        <Segment attached='bottom' style={{height: '10%'}} as={Menu} size='big'>
          <Menu.Item
            header
            icon='database'
            iconPosition='left'
            content='CReSCENT'
          />
          <Modal
            trigger={
              <Menu.Item content={
                <Button color='red' content='Upload' icon='upload'/>
              } />
            }
            header='Upload Workflow Inputs Here'
            content='Upload API stuff here'
            actions={
              [
                <Button key='submit' content='Submit uploads' />
              ]
            }
          />
          <Menu.Item />
          {
            ['Alignment', 'Normalization', 'Clustering'].map(
              step => (
                <Dropdown key={step} text={step} button pointing='top'>
                  <Dropdown.Menu>
                    <Button as={Dropdown.Item} onClick={() => setVisContent(step)} content='Inspect' />
                    <Modal
                      trigger={
                        <Button key={step} as={Dropdown.Item} content='Parameters' />
                      }
                      header={step}
                      content={`${step} parameters`}
                      actions={[
                        <Button key='close' content='close' />
                      ]}
                    />



                  </Dropdown.Menu>
                </Dropdown>

              )
            )
          }
        </Segment>
  )
}

const App = ({

}) => {
    const [visContent, setVisContent] = useState('Home')
    return (
      <Segment style={{height: '100%'}}>
        <VisualizationComponent
          visContent={visContent}
          setVisContent={setVisContent}
        />
        <MenuComponent
          visContent={visContent}
          setVisContent={setVisContent}
        />
      </Segment>
    )
  }

  export default App
